<?php\n// adjust.php\nif (isset($_GET['num']) && isset($_GET['inc'])) {\n    $number = intval($_GET['num']);\n    $increment = intval($_GET['inc']);\n    // Subtract 1 from the number and increment parts separately if needed\n    $adjustedNumber = ($number - 1) . '-' . $increment;\n    $name = urlencode($_GET['name']);\n\n    header("Location: main.php/d/{$adjustedNumber}/{$name}");\n    exit();\n} else {\n    http_response_code(400);\n    echo "Bad Request";\n}\n?>