use PHPUnit\Framework\TestCase;\n\nclass ParserTest extends TestCase\n{\n    public function testFetchAndParse()\n    {\n        // Create a mock object for the Parser class\n        $mockParser = $this->getMockBuilder('Parser')\n            ->onlyMethods(['loadUrl', 'parse'])\n            ->getMock();\n\n        // Set up expectations for the loadUrl and parse methods\n        $mockParser->expects($this->once())\n            ->method('loadUrl')\n            ->with('http://url');\n\n        $mockParser->expects($this->once())\n            ->method('parse');\n\n        // Call the method on your mock object\n        $mockParser->fetchAndParse('http://url');\n    }\n}