<?php\nsession_start(); // Start session for login process\n\n// Assume we have some function to validate user credentials\nfunction isUserValid($username, $password) {\n    // This should usually involve checking against a database securely\n    return ($username === "admin" && $password === "securepassword");\n}\n\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $username = $_POST['username'];\n    $password = $_POST['password'];\n\n    if (isUserValid($username, $password)) {\n        // Reset session to prevent fixation\n        session_regenerate_id(true);\n        \n        // Set user data in the session\n        $_SESSION['user'] = $username;\n        echo "Login successful!";\n    } else {\n        echo "Invalid username or password.";\n    }\n}\n?>