<?php\nini_set('memory_limit', '1M'); // Ensure a very low memory limit to verify memory usage\n\n$filename = 'example.jpg';\nlist($width, $height, $type) = getimagesize($filename);\n\n// Convert type constant to MIME type if needed\n$imageTypes = [\n    IMAGETYPE_JPEG => 'image/jpeg',\n    IMAGETYPE_PNG  => 'image/png',\n];\n\n$mimeType = isset($imageTypes[$type]) ? $imageTypes[$type] : 'unknown';\n\necho "Width: $width, Height: $height, MIME Type: $mimeType\n";\n?>