// Example of setting domain explicitly\n\n$domains = [\n    'website1.com' => 'website1.com',\n    'shopping.example.com' => 'shopping.example.com'\n];\n\n$currentDomain = $_SERVER['HTTP_HOST'];\n\nif (array_key_exists($currentDomain, $domains)) {\n    session_set_cookie_params(0, '/', '.' . $domains[$currentDomain]); // Use domain mapping\n} else {\n    // Handle unknown domains or set a default behavior\n    session_set_cookie_params(0, '/', 'defaultdomain.com');\n}\n\nsession_start();