function getWordNGrams($text, $n = 2) {\n    // Remove punctuation and convert text to lowercase\n    $text = strtolower(preg_replace('/[^\p{L}\s]/u', '', $text));\n    \n    // Split the text into words\n    $words = explode(' ', trim($text));\n    \n    // Initialize array to hold n-grams\n    $ngrams = [];\n    \n    // Generate n-grams\n    for ($i = 0; $i <= count($words) - $n; $i++) {\n        $ngram = [];\n        for ($j = $i; $j < $i + $n; $j++) {\n            $ngram[] = $words[$j];\n        }\n        // Add n-gram to array\n        $ngrams[] = implode(' ', $ngram);\n    }\n    \n    return $ngrams;\n}\n\n$text = "I live in NY";\nprint_r(getWordNGrams($text));