function sanitizeXML($xmlString) {\n    // Replace < with &lt; and > with &gt; to prevent XML tag conflicts\n    $sanitized = str_replace(['<', '>'], ['&lt;', '&gt;'], $xmlString);\n    return $sanitized;\n}\n\n$xmlData = '<root><tag>invalid <3 data</tag></root>'; // Example of malformed XML\n$safeXMLData = sanitizeXML($xmlData);\n\ntry {\n    $xml = new SimpleXMLElement($safeXMLData);\n    echo "Parsing successful!";\n} catch (Exception $e) {\n    echo "Failed to parse XML: " . $e->getMessage();\n}