<?php\n\n   require 'vendor/autoload.php';\n\n   use MathPHP\LinearAlgebra\Matrix;\n\n   // Define a matrix\n   $matrix = [\n       [1, 2],\n       [3, 4]\n   ];\n\n   // Create a Matrix object\n   $A = new Matrix($matrix);\n\n   // Transpose the matrix\n   $B = $A->transpose();\n   echo "Transpose:\n";\n   print_r($B->getMatrix());\n\n   // Calculate the determinant\n   $determinant = $A->det();\n   echo "\nDeterminant: " . $determinant . "\n";\n\n   // Invert the matrix\n   try {\n       $C = $A->inverse();\n       echo "\nInverse:\n";\n       print_r($C->getMatrix());\n   } catch (Exception $e) {\n       echo "\nError inverting matrix: " . $e->getMessage() . "\n";\n   }