$profiler = new Zend_Db_Profiler(true);  // true enables all profiling\n   $db->setQueryProfiler($profiler);\n\n   $stmt = $db->query('SELECT * FROM your_table');\n   $result = $stmt->fetchAll();\n\n   // Get query profiles and print details\n   $profi leOutput = '';\n   foreach ($profiler->getQueryProfiles() as $profile) {\n       $profi leOutput .= 'Query: ' . $profile->getQuery() . "\n";\n       $profi leOutput .= 'Execution time: ' . $profile->getElapsedSecs() . " secs\n";\n       $profi leOutput .= '---------------------\n';\n   }\n   echo $profi leOutput;