<?php\n$html = '<p>I am using the PHP 5 <span style="font-style:bold">Tidy</span> class to format html.</p>';\n\n$config = array(\n    'indent' => true,\n    'output-xhtml' => true,\n    'wrap' => 200,\n    'clean' => false, // Do not clean the document\n    'drop-empty-paras' => false, // Keep empty paragraphs\n    'show-body-only' => true, // Only output body content\n);\n\n$tidy = new tidy();\n$tidy->parseString($html, $config, 'utf8');\n$tidy->cleanRepair();\n\necho $tidy;\n?>