function logActivityInDB($pdo, $userId, $action) {\n    try {\n        $stmt = $pdo->prepare("INSERT INTO activity_logs (user_id, action, timestamp) VALUES (:userId, :action, NOW())");\n        $stmt->bindParam(':userId', $userId);\n        $stmt->bindParam(':action', $action);\n        $stmt->execute();\n    } catch (PDOException $e) {\n        // Handle error appropriately\n    }\n}