<?php\n        // Parse RSS and get the first two items\n        $rss = simplexml_load_file('your_rss_feed_url.xml');\n        $newsItems = array();\n\n        for ($i=0; $i<2 && isset($rss->channel->item[$i]); $i++) {\n            $title = (string) $rss->channel->item[$i]->title;\n            $description = (string) $rss->channel->item[$i]->description;\n            $newsItems[] = "$title\n$description";\n        }\n\n        echo implode('|', $newsItems);\n    ?>