<?php\nfunction split_path($path = null) {\n    if ($path === null) {\n        $path = getenv('PATH');\n    }\n    \n    // Determine the OS dependent path separator\n    $separator = PHP_OS_FAMILY === 'Windows' ? ';' : ':';\n    \n    // Split the path into an array\n    return explode($separator, $path);\n}\n\n// Example usage\n$pathComponents = split_path();\nprint_r($pathComponents);\n?>