<?php\n\nfunction generateUniqueSlug($pdo) {\n    // Generate a unique base string using random_bytes for high entropy\n    $baseString = bin2hex(random_bytes(6));\n    \n    // Ensure uniqueness by checking against the existing slugs in the database\n    do {\n        $stmt = $pdo->prepare("SELECT COUNT(*) FROM records WHERE slug = :slug");\n        $stmt->execute([':slug' => $baseString]);\n    } while ($stmt->fetchColumn() > 0);\n    \n    return $baseString;\n}\n\n// Example usage:\ntry {\n    // Connect to your database\n    $pdo = new PDO('mysql:host=localhost;dbname=testdb', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Generate a unique slug\n    $uniqueSlug = generateUniqueSlug($pdo);\n\n    // Insert record with the generated slug (assuming you have an associative array of data)\n    $data = ['name' => 'example name', 'description' => 'example description'];\n    $stmt = $pdo->prepare("INSERT INTO records (slug, name, description) VALUES (:slug, :name, :description)");\n    $stmt->execute([':slug' => $uniqueSlug, ':name' => $data['name'], ':description' => $data['description']]);\n\n    echo "Record created with slug: " . $uniqueSlug;\n} catch (PDOException $e) {\n    die("Could not connect to the database $dbname :" . $e->getMessage());\n}\n\n?>