try {\n    $pdo = new PDO("mysql:host=your_host;dbname=your_db", "username", "password");\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    if (isset($_REQUEST['EDIT'])) {\n        $stmt = $pdo->prepare("UPDATE service SET Start_date=:Date1, Venue=:Venue, Facilitator=:Facilitator WHERE ServiceID = :id");\n        $stmt->execute([\n            ':Date1' => $_REQUEST['date1'],\n            ':Venue' => isset($_REQUEST['venTxt']) ? ($_REQUEST['venTxt']) : "",\n            ':Facilitator' => isset($_REQUEST['faciTxt']) ? ($_REQUEST['faciTxt']) : "",\n            ':id' => $_REQUEST['id']\n        ]);\n\n        echo '<script type="text/javascript">';\n        echo 'alert("Changes have been saved!");';\n        echo 'window.location="Admin_RecSchedMapLst.php";';\n        echo '</script>';\n    }\n} catch (PDOException $e) {\n    die('Error: ' . $e->getMessage());\n}