public function afterSave($event, $entity, $options)\n{\n    if ($entity->isDirty('source_id')) {\n        // The 'source_id' field was modified.\n        $originalSourceId = $entity->getOriginal('source_id');\n        $newSourceId = $entity->source_id;\n\n        // Update cache for the old source\n        if ($originalSourceId) {\n            // Decrement the Person_Count on the original source\n            $this->Sources->decrementCounter($originalSourceId, 'Person_Count');\n        }\n\n        // Update cache for the new source\n        if ($newSourceId) {\n            // Increment the Person_Count on the new source\n            $this->Sources->incrementCounter($newSourceId, 'Person_Count');\n        }\n    }\n}