$stmt = $pdo->prepare("SELECT * FROM Activities A JOIN Followers F ON A.user_id = F.follower_id WHERE F.user_id = ? ORDER BY A.id DESC");\n$stmt->execute([$user_id]);\n$activities = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// PHP grouping logic (example)\n$groupedActivities = [];\nforeach ($activities as $activity) {\n    if (!isset($groupedActivities[$activity['activity_type']])) {\n        $groupedActivities[$activity['activity_type']] = [];\n    }\n    $groupedActivities[$activity['activity_type']][] = $activity;\n}\n\n// Example of counting activities by type in PHP\n$countsByType = array_map(function($items) {\n    return count($items);\n}, $groupedActivities);