function update_roster() {\n    $url = "http://example.com/";\n    $userID = "xx";\n    $apikey = "xxx";\n    $passfields = "userID=$userID&apikey=$apikey";\n\n    // Choose either file output or return transfer, not both\n    $useFileOutput = false; // Set to true if you want to write directly to a file\n\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_POST, 1);\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_POSTFIELDS, $passfields);\n\n    if ($useFileOutput) {\n        $file = fopen("roster.csv", "w+");\n        curl_setopt($ch, CURLOPT_FILE, $file);\n        curl_exec($ch);\n        fclose($file);\n        $variable_in_question = null; // No data returned when using CURLOPT_FILE\n    } else {\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n        $variable_in_question = curl_exec($ch);\n        // Now you can process the CSV content in memory\n        if ($variable_in_question) {\n            $lines = split("\r\n", $variable_in_question); // Use double quotes here\n            // Process your lines as needed\n        }\n    }\n\n    curl_close($ch);\n\n    return $variable_in_question;\n}