<?php\nfunction getTablesStartingWith($prefix) {\n    // Connect to your database\n    $conn = new mysqli("host", "username", "password", "database");\n\n    // Check connection\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    // Get list of tables starting with the specified prefix\n    $tables = [];\n    $sql = "SHOW TABLES LIKE '{$prefix}%'";\n    $result = $conn->query($sql);\n\n    if ($result !== false) {\n        while ($row = $result->fetch_row()) {\n            $tables[] = $row[0];\n        }\n    }\n\n    // Close the connection\n    $conn->close();\n\n    return $tables;\n}\n\nfunction queryTablesForSectionId($sectionId, $prefix) {\n    // Get table names starting with the given prefix\n    $tables = getTablesStartingWith($prefix);\n\n    if (empty($tables)) {\n        echo "No tables found.";\n        return [];\n    }\n\n    // Construct a UNION SQL query for all tables\n    $queries = array_map(function ($table) use ($sectionId) {\n        return "SELECT * FROM {$table} WHERE section_id = '{$sectionId}'";\n    }, $tables);\n\n    $unionQuery = implode(' UNION ', $queries);\n\n    // Execute the union query\n    $conn = new mysqli("host", "username", "password", "database");\n\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    $result = $conn->query($unionQuery);\n\n    $rows = [];\n    if ($result !== false) {\n        while ($row = $result->fetch_assoc()) {\n            $rows[] = $row;\n        }\n    } else {\n        echo "Error executing query: " . $conn->error;\n    }\n\n    // Close the connection\n    $conn->close();\n\n    return $rows;\n}\n\n// Usage\n$sectionIdToFind = 123; // Example section_id you are searching for\n$tablePrefix = 'mod_';  // Prefix of your module tables\n\n$results = queryTablesForSectionId($sectionIdToFind, $tablePrefix);\n\nforeach ($results as $result) {\n    print_r($result);\n}\n?>