<?php\n// Check if the image ID is provided and safe\nif (isset($_GET['id']) && ctype_digit($_GET['id'])) {\n    // Map the ID to a real file path. Ensure this mapping is secure.\n    $imageId = $_GET['id'];\n    $filePath = "images/{$imageId}.jpg"; // Example path, adjust as necessary\n\n    // Check if the file exists and is accessible\n    if (file_exists($filePath)) {\n        // Set the appropriate content type header for images\n        header('Content-Type: image/jpeg');\n        // Output the image\n        readfile($filePath);\n        exit; // Ensure no other output follows\n    } else {\n        http_response_code(404); // If the file does not exist, send a 404 Not Found response\n    }\n} else {\n    http_response_code(400); // Bad request if ID is missing or invalid\n}\n?>