class Some_model extends CI_Model {\n    public function get_data() {\n        // Try to get cached data first.\n        $data = $this->cache->memcached->get('some_key');\n        if ($data) {\n            return $data;\n        }\n\n        // Data is not in cache, fetch it from database or other source.\n        $data = $this->db->where(['status' => 1])->get('items')->result();\n\n        // Cache the data for next time after fetching.\n        $this->cache->memcached->save('some_key', $data, (60 * 60)); // Cache for one hour.\n\n        return $data;\n    }\n}