function countWorkDays($startDate, $endDate) {\n    // Convert string dates to DateTime objects\n    $start = new DateTime($startDate);\n    $end = new DateTime($endDate);\n    \n    // Modify end date to exclude it from counting if it's a weekend\n    $end->modify('-1 day');\n\n    $interval = $start->diff($end);\n    $days = $interval->d + ($interval->y * 365) + ($interval->m * 30);\n\n    // Iterate over the range and count work days (Monday to Friday)\n    $workDaysCount = 0;\n    for ($current = clone $start; $current <= $end; $current->modify('+1 day')) {\n        if ($current->format('N') < 6) { // 'N' gives you a number from 1 (Monday) to 7 (Sunday)\n            $workDaysCount++;\n        }\n    }\n\n    return $workDaysCount;\n}\n\n// Example usage:\necho countWorkDays('2023-10-01', '2023-10-15');