// Get the default autoloader instance\n$autoloader = Zend_Loader_Autoloader::getInstance();\n\n// Define the path where your Open Flash Chart files are located\n$ofcPath = APPLICATION_PATH . '/../library/OpenFlashChart2';\n\n// Exclude this path from the autoloader's include paths\n$autoloader->setFallbackAutoloader(false);\n$autoloader->unregisterAutoload();\n$autoloader->autoload('Zend_Loader_Autoloader_Resource');\n$autoloader->registerNamespace('');\n\n// Register a custom autoload function for Open Flash Chart\nspl_autoload_register(function($class) use ($ofcPath) {\n    // Convert the class name to lowercase and replace underscores with slashes\n    $file = strtolower(str_replace('_', '/', $class)) . '.php';\n    $fullPath = $ofcPath . '/' . $file;\n\n    // Check if the file exists and require it\n    if (file_exists($fullPath)) {\n        require_once $fullPath;\n    }\n});