<?php\n// Assuming $db is your PDO database connection\n\n$searchTerm = '%'. $_GET['search'] .'%'; // Example search term from user input\n\ntry {\n    $stmt = $db->prepare("\n        SELECT 'users' as source, id, profile_text AS excerpt \n        FROM users \n        WHERE username LIKE :search OR profile_text LIKE :search \n        \n        UNION\n        \n        SELECT 'uploads' as source, id, description AS excerpt \n        FROM uploads \n        WHERE title LIKE :search OR description LIKE :search \n        \n        UNION\n        \n        SELECT 'sets' as source, id, description AS excerpt \n        FROM sets \n        WHERE description LIKE :search \n        \n        UNION\n        \n        SELECT 'comments' as source, id, text AS excerpt \n        FROM comments \n        WHERE text LIKE :search\n    ");\n\n    // Bind the search term to the query parameters\n    $stmt->bindParam(':search', $searchTerm);\n\n    // Execute the query\n    $stmt->execute();\n\n    // Fetch results\n    $results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    foreach ($results as $result) {\n        echo "Source: " . htmlspecialchars($result['source']) . ", ID: " . htmlspecialchars($result['id']) . ", Excerpt: " . htmlspecialchars($result['excerpt']) . "<br>";\n    }\n\n} catch (PDOException $e) {\n    echo 'Error: ' . $e->getMessage();\n}\n?>