<?php\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n// Prepare and bind\n$stmt = $mysqli->prepare("INSERT INTO `reunie` (`naam`, `van`,`nooiensvan`, `selfoon`, `email`, `bywoon`, `metgesel_naam`, `spesifieke_onderwysers`, `spesifieke_musiek`, `bydrae`, `voorstelle`) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)");\n$stmt->bind_param("sssssssssss", $naam, $van, $nooiensvan, $selfoon, $email, $bywoon, $metgesel, $spesifiekeOnderwysers, $musiek, $bydrae, $voorstelle);\n\n// Execute insert\nif ($stmt->execute()) {\n    // Get the last inserted ID\n    $insertId = $mysqli->insert_id;\n\n    // Prepare and bind for selecting the id\n    $selectStmt = $mysqli->prepare("SELECT `id` FROM `reunie` WHERE `naam` = ? AND `van` = ? ORDER BY `id` DESC LIMIT 1");\n    $selectStmt->bind_param("ss", $naam, $van);\n    \n    // Execute select\n    if ($selectStmt->execute()) {\n        $result = $selectStmt->get_result();\n        $row = $result->fetch_assoc();\n        if ($row) {\n            $selectedId = $row['id'];\n            // Insert selected id into another table or perform other actions\n            echo "Selected ID: " . $selectedId;\n        }\n    } else {\n        die($selectStmt->error);\n    }\n\n    $selectStmt->close();\n} else {\n    die($stmt->error);\n}\n\n$stmt->close();\n$mysqli->close();\n?>