<?php\n\n// Assuming $jsonData is your JSON string containing product data array\n$jsonData = '[\n    {"sku": "12345", "name": "Product A", "description": "This is Product A"},\n    {"sku": "67890", "name": "Product B", "description": "This is Product B"}\n]';\n\n$dataArray = json_decode($jsonData, true); // Convert JSON string to PHP array\n\n// Connect to MySQL\n$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n$pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n// Start transaction\n$pdo->beginTransaction();\n\ntry {\n    $query = "INSERT INTO products (sku, name, description) VALUES (:sku, :name, :description)";\n    $stmt = $pdo->prepare($query);\n\n    foreach ($dataArray as $product) {\n        $stmt->execute([\n            ':sku' => $product['sku'],\n            ':name' => $product['name'],\n            ':description' => $product['description']\n        ]);\n    }\n\n    // Commit transaction\n    $pdo->commit();\n} catch (Exception $e) {\n    // Rollback the transaction on error\n    $pdo->rollBack();\n    throw $e;\n}\n\n?>