<?php\nfunction verifyHMAC($data, $receivedHmac, $secretKey) {\n    $expectedHmac = hash_hmac('sha256', $data, $secretKey);\n    return hash_equals($expectedHmac, $receivedHmac); // Use hash_equals for timing attack safety\n}\n\n$scoreData = file_get_contents('php://input');\n$receivedHmac = $_SERVER['HTTP_X_HMAC']; // Assuming HMAC is sent in a custom HTTP header\n$secretKey = 'yourSecretKey'; // Must match the client-side secret\n\nif (verifyHMAC($scoreData, $receivedHmac, $secretKey)) {\n    // Process score data\n    $data = json_decode($scoreData);\n    echo "Score received securely!";\n} else {\n    http_response_code(403); // Forbidden\n    echo "Invalid HMAC";\n}\n?>