<?php\nrequire_once('vendor/autoload.php');\n\nuse setasign\Fpdi\Tcpdf\Fpdi;\n\n// Create new FPDI object\n$pdf = new Fpdi();\n\n// Add a page from an existing PDF file\n$pageCount = $pdf->setSourceFile('form.pdf');\n$tplIdx = $pdf->importPage(1);\n$pdf->AddPage();\n$pdf->useTemplate($tplIdx, 10, 10, 200);\n\n// Now write some text into the form fields (you need to know their names)\n$pdf->SetFont('helvetica', '', 12);\n$pdf->SetXY(85, 78); // Adjust coordinates to your field locations\n$pdf->Write(0, 'John Doe');\n\n// Output the new PDF to a file or browser\n$pdf->Output('filled_and_flattened.pdf', 'F'); // Use 'I' in place of 'F' for browser output\n\n?>