function getNestedArrayValue(&$array, $path) {\n    // Split the path by '/' to get each level as an array key\n    $keys = explode('/', trim($path, '/'));\n    \n    // Traverse the array based on the keys\n    foreach ($keys as $key) {\n        if (isset($array[$key])) {\n            $array = &$array[$key]; // Move the reference down one level\n        } else {\n            throw new InvalidArgumentException("Path not found in the array: " . $path);\n        }\n    }\n    \n    return $array;\n}\n\n// Example usage:\n$pathToFolder = "docs/_c/folder/_c/folder/1"; // This path leads to Subfolder2 if it exists\n$newfile = ['_c' => ['name' => ['_v' => 'NewFile.txt']]];\n\ntry {\n    $targetArray = getNestedArrayValue($array, $pathToFolder);\n    array_push($targetArray, $newfile);\n} catch (InvalidArgumentException $e) {\n    echo "Error: " . $e->getMessage();\n}