<?php\n\nfunction isValidCurrency($value) {\n    return preg_match('/^\d+(\.\d{2})?$/', $value);\n}\n\n// Test cases\nvar_dump(isValidCurrency("10"));     // bool(true)\nvar_dump(isValidCurrency("100"));    // bool(true)\nvar_dump(isValidCurrency("100.00")); // bool(true)\nvar_dump(isValidCurrency("abc"));    // bool(false)\nvar_dump(isValidCurrency("100,00")); // bool(false)\nvar_dump(isValidCurrency("+100"));   // bool(false)\n\n?>