<?php \n// Display categories\n$display_categories = array( 4, 7, 8, 9, 21, 1 ); \nforeach ( $display_categories as $category ) { \n    ?>\n    <div>\n        <?php \n        // Assuming you want to display the name of each category\n        echo get_cat_name($category);\n        ?>\n        <!-- Add more category-related content here -->\n    </div>\n<?php } \n\n// Reset post data in case this is a secondary loop\nwp_reset_postdata();\n\n// Custom query for posts ordered by date descending\n$args = array(\n    'cat' => '-1', // Excluding specific categories, adjust as needed\n    'posts_per_page' => 15,\n    'orderby' => 'date',\n    'order' => 'DESC'\n);\n$q = new WP_Query( $args );\n\nif ( $q->have_posts() ) : \n    while ( $q->have_posts() ) : $q->the_post(); ?>\n        <div>\n            <?php the_title(); // Display post title or other content as needed ?>\n        </div>\n<?php endwhile; \n\n// Reset post data after custom query\nwp_reset_postdata();\n\nelse: \n    echo 'No posts found.';\nendif; \n?>