try {\n    $pdo = new PDO("mysql:host=your_host;dbname=your_db", "username", "password");\n    $stmt = $pdo->prepare(\n        "UPDATE auth SET username=:username, password=:password, studycode=:studycode, description=:description, server=:server WHERE `index`=:id"\n    );\n    $stmt->execute([\n        ':username' => $username,\n        ':password' => $password,\n        ':studycode' => $studycode,\n        ':description' => $description,\n        ':server' => $server,\n        ':id' => $id\n    ]);\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}