private function _getAllIngredients($animal = null, $type = null) {\n    $ingredients = null;\n    if ($animal != null && $type != null) {\n        $query = 'SELECT id, name, brief_description, description, \n                         food_type, ingredient_type, image, price,\n                         created_on, updated_on\n                    FROM ingredient\n                   WHERE food_type = :foodType AND ingredient_type = :ingredientType';\n                   \n        // Assuming $this->pdo is a PDO instance\n        $stmt = $this->pdo->prepare($query);\n        $stmt->bindParam(':foodType', $animal);\n        $stmt->bindParam(':ingredientType', $type);\n        \n        if ($stmt->execute()) {\n            $rows = $stmt->fetchAll(PDO::FETCH_ASSOC);\n            // Process your rows\n        }\n    }\n}