<table width="100%" class="tborder">\n    <tr>\n        <th class="tcat">Name</th>\n        <th class="tcat">Reply</th>\n    </tr>\n    <?php\n        $colour = "1";\n        // Prepare the query and execute it.\n        $result = mysqli_query($db, "SELECT * from tbl_bugresponse WHERE bugid = $id ORDER BY time");\n        \n        // Fetch each row in the loop.\n        while ($row = mysqli_fetch_assoc($result)) {\n            if ($colour == "1") {\n                echo "<tr class='alt1'>";\n                $f1 = $row['by'];\n                $f2 = $row['content'];\n                $colour = "2";\n            } else {\n                echo "<tr class='alt2'>";\n                $f1 = $row['by'];\n                $f2 = $row['content'];\n                $colour = "1";\n            }\n    ?>\n            <td><?php echo htmlspecialchars($f1); ?></td>\n            <td><?php echo htmlspecialchars($f2); ?></td>\n        </tr>\n    <?php\n        }\n    ?>\n</table>