<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $content = $_POST['content'];\n    \n    // Sanitize input data before using it\n    $sanitizedContent = htmlspecialchars($content, ENT_QUOTES, 'UTF-8');\n    \n    // Example of using prepared statements with PDO\n    try {\n        $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n        $stmt = $pdo->prepare("INSERT INTO `user_comments` (`comment_text`) VALUES (:content)");\n        $stmt->execute([':content' => $sanitizedContent]);\n        echo "Your comment has been saved.";\n    } catch (PDOException $e) {\n        die("Database error: " . $e->getMessage());\n    }\n} else {\n    echo "Invalid request method.";\n}\n?>