$connectionOptions = array(\n       "UID" => "your_username",\n       "PWD" => "your_password",\n       "DATABASE" => "your_database"\n   );\n   \n   //Establishes the PDO connection using ODBC and FreeTDS\n   try {\n       $conn = new PDO("odbc:Driver=FreeTDS;Server=your_server_address;Port=1433;Database=your_database;", $connectionOptions["UID"], $connectionOptions["PWD"]);\n       $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n   } catch (PDOException $e) {\n       die("Could not connect to the database $db_name :" . $e->getMessage());\n   }