function getSubdomain($hostname) {\n    // Regular expression pattern to match the subdomain\n    $pattern = '/^(?:([a-zA-Z0-9\-]+)\.)?[^\.]+\.[a-zA-Z]{2,}$/';\n    \n    if (preg_match($pattern, $hostname, $matches)) {\n        // Return the subdomain if it exists\n        return isset($matches[1]) ? $matches[1] : '';\n    }\n    \n    // Return an empty string if no match is found\n    return '';\n}\n\n// Test cases\n$hosts = [\n    "token.domain.com",\n    "token.domain.com/",\n    "token.domain.com/index.php",\n    "token.domain.com/folder/index.php",\n    "token.domain.com/folder/subfolder",\n    "token.domain.com/folder/subfolder/index.php",\n    "domain.com",\n    "domain.com/",\n];\n\nforeach ($hosts as $host) {\n    echo "Host: $host, Subdomain: " . getSubdomain($host) . "\n";\n}