<?php\n\nrequire 'JSMin-php.php';\n\nfunction minifyJavaScript($javascript) {\n    return JSMin::minify($javascript);\n}\n\n// Example usage:\n$jsContent = file_get_contents('scripts.js');\n$compressedJs = minifyJavaScript($jsContent);\n\necho $compressedJs; // Outputs the compressed JavaScript\n\n// Optionally, write to a new file\nfile_put_contents('scripts-min.js', $compressedJs);\n?>