function getNetworkAddress($ip, $subnet) {\n    // Convert IP to long format\n    $ipLong = ip2long($ip);\n    \n    // Calculate the subnet mask in long format\n    $maskLong = ~((1 << (32 - $subnet)) - 1);\n    \n    // Calculate network address\n    $networkLong = $ipLong & $maskLong;\n    \n    // Convert back to dotted decimal format\n    return long2ip($networkLong);\n}\n\n$ip = '193.95.221.54';\n$subnet = 22;\n\necho getNetworkAddress($ip, $subnet); // Outputs: 193.95.220.0