if ( defined( 'MW_SUPPORTS_PARSERFIRSTCALLINIT' ) ) {\n    $wgHooks['ParserFirstCallInit'][] = 'tagregister';\n} else { // Fallback for older versions of MediaWiki\n    $wgExtensionFunctions[] = 'tagregister';\n}\n\nfunction tagregister() {\n    global $wgParser;\n    // Register tag hooks with the parser\n    $wgParser->setHook('tag1', 'tag1func');\n    $wgParser->setHook('tag2', 'tag2func');\n    return true; // Indicate successful hook registration\n}\n\n// Define the function for the custom <tag1> tag\nfunction tag1func($input, $params, $parser, $frame) {\n    // Return a simple string to insert into the page output\n    return "It called me";\n}\n\n// Define the function for the custom <tag2> tag\nfunction tag2func($input, $params, $parser, $frame) {\n    // Return another simple string to insert into the page output\n    return "It called me -- 2";\n}