function showCombinations($string, $traits, $i)\n{\n    if ($i >= count($traits))\n        echo trim($string) . "\n";  // Use newline here\n    else {\n        foreach ($traits[$i] as $trait)\n            showCombinations("$string$trait", $traits, $i + 1);\n    }\n}\n\n// In your form output:\n<form name=form1 method=post action=''>\n<textarea>\n<?php \necho htmlspecialchars(implode("", ShowCombinations('', $cat, 0))); // Ensure proper HTML output\n?>\n</textarea>\n</form>