function save() {\n    /*\n            Here we do either a create or\n            update operation depending\n            on the value of the id field.\n            Zero means create, non-zero\n            update\n    */\n\n    try {\n        $db = parent::getConnection();\n        if($this->id == 0 ) {\n            $query = 'INSERT INTO articles (modified, username, url, title, description, points) VALUES (:modified, :username, :url, :title, :description, :points)';\n            $stmt = $db->prepare($query);\n            $stmt->execute([\n                ':modified' => $this->getModified(),\n                ':username' => $this->username,\n                ':url' => $this->url,\n                ':title' => $this->title,\n                ':description' => $this->description,\n                ':points' => $this->points\n            ]);\n            createRSS(); //**** rss function****\n        } else {\n            $query = "UPDATE articles SET modified = NOW(), username = :username, url = :url, title = :title, description = :description, points = :points WHERE id = :id";\n            $stmt = $db->prepare($query);\n            $stmt->execute([\n                ':username' => $this->username,\n                ':url' => $this->url,\n                ':title' => $this->title,\n                ':description' => $this->description,\n                ':points' => $this->points,\n                ':id' => $this->id\n            ]);\n        }\n\n        if($this->id == 0 ) {\n            $this->id = $db->lastInsertId();\n        }\n    } catch(PDOException $e) {\n        error_log("Database error: " . $e->getMessage());\n        throw new Exception("An error occurred while saving the article.");\n    } catch(Exception $e){\n        error_log("General error: " . $e->getMessage());\n        throw $e;\n    }\n}