<?php\nfunction getRecentErrorLogEntries($filePath, $linesNumber = 10) {\n    // Check if file exists or is readable\n    if (!file_exists($filePath) || !is_readable($filePath)) {\n        return "File does not exist or is not readable.";\n    }\n\n    // Read the entire file content into an array of lines\n    $lines = file($filePath, FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);\n\n    // Get the last number of lines specified\n    $recentLines = array_slice($lines, -$linesNumber);\n\n    return array_reverse($recentLines); // Reverse to have latest on top\n}\n\n// Path to your Apache error log\n$errorLogPath = '/var/log/apache2/error.log';\n\n$recentErrors = getRecentErrorLogEntries($errorLogPath);\n\necho "<h1>Most Recent Error Log Entries</h1>";\nforeach ($recentErrors as $error) {\n    echo htmlspecialchars($error) . "<br>\n";\n}\n?>