<?php\n   // Initialize a cURL session for fetching Google Calendar's HTML\n   $ch = curl_init('https://calendar.google.com/calendar/embed?src=your_calendar_id&ctz=Your/Timezone');\n   curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n   \n   // Execute and retrieve the content\n   $htmlContent = curl_exec($ch);\n   \n   // Close cURL session\n   curl_close($ch);\n   \n   // Use a regular expression to find and replace links\n   // Adjust this regex according to your specific link structure\n   $modifiedHtmlContent = preg_replace('/(http(s)?:\/\/yourwebsite\.com\/)(.+)/', '$1$3', $htmlContent);\n   \n   echo $modifiedHtmlContent;\n   ?>