// PHP code on Linux interacting with a SOAP service for COM object\n$wsdl = "http://windows-ip/service.asmx?WSDL"; // WSDL URL of the SOAP service\n\n$client = new SoapClient($wsdl);\ntry {\n    $result = $client->YourServiceMethod(); // Method provided by the SOAP service\n    echo "Result: ";\n    print_r($result);\n} catch (SoapFault $e) {\n    echo "Error: " . $e->getMessage();\n}