ini_set('memory_limit', '512M'); // Adjust memory limit as necessary\nset_time_limit(300); // Increase timeout to 5 minutes or more\n\n$secretfile = '/path/to/file.mp3'; // Ensure correct path here\n$dname = basename($secretfile, '.mp3');\n\nif (file_exists($secretfile) && is_readable($secretfile)) {\n    header("Pragma: public");\n    header("Cache-Control: must-revalidate, post-check=0, pre-check=0");\n    header("Cache-Control: private", false);\n    header("Content-Type: application/octet-stream"); // Use correct Content-Type\n    header("Content-Disposition: attachment; filename=\"$dname.mp3\"");\n    header("Content-Transfer-Encoding: binary");\n    header("Content-Length: " . filesize($secretfile));\n    \n    ob_clean(); // Clean output buffer if enabled\n    flush(); // Flush system buffers\n    \n    readfile($secretfile);\n} else {\n    http_response_code(404); // Report error if file not found\n    echo "File not found.";\n}