<?php\n\nfunction calculateDistance($origin, $destination) {\n    $apiKey = 'YOUR_API_KEY'; // Replace with your Google Maps API Key\n    $url = "https://maps.googleapis.com/maps/api/distancematrix/json?origins=" . urlencode($origin) . "&destinations=" . urlencode($destination) . "&mode=driving&key=" . $apiKey;\n    \n    $curl = curl_init();\n    curl_setopt($curl, CURLOPT_URL, $url);\n    curl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);\n    $response = curl_exec($curl);\n    curl_close($curl);\n\n    $data = json_decode($response, true);\n    if ($data['status'] == 'OK') {\n        return [\n            'distance' => $data['rows'][0]['elements'][0]['distance']['text'],\n            'duration' => $data['rows'][0]['elements'][0]['duration']['text']\n        ];\n    } else {\n        return null; // Handle errors\n    }\n}\n\n$origin = "Champs-Elysées, Paris";\n$destination = "Eiffel Tower, Paris";\n\n$result = calculateDistance($origin, $destination);\nif ($result) {\n    echo "Distance: " . $result['distance'] . "\n";\n    echo "Duration: " . $result['duration'] . "\n";\n} else {\n    echo "Failed to get distance.\n";\n}\n\n?>