use Drupal\user\Entity\User;\n\n// Replace 'your_role_name' with the actual machine name of your role.\n$role_name = 'your_role_name';\n\n// Create a user query and filter by role.\n$query = \Drupal::entityQuery('user')\n  ->condition('roles', $role_name);\n\n// Execute the query to get user IDs.\n$user_ids = $query->execute();\n\n// Load the user entities.\n$users = User::loadMultiple($user_ids);\n\n// Optionally, you can iterate over the users and perform actions.\nforeach ($users as $user) {\n  // Perform actions on each user entity.\n  \Drupal::logger('my_module')->notice('User: @name (@uid)', ['@name' => $user->getDisplayName(), '@uid' => $user->id()]);\n}