if (!isset($_SESSION['dataentry'])) {\n    $stmt = $db->prepare("SELECT participationcode, modulearray, wavenum FROM mng_wave WHERE wave_id=?");\n    $stmt->bind_param("i", $_GET['wave_id']);\n    $stmt->execute();\n    $result_taskinfo = $stmt->get_result();\n    $row_taskinfo = $result_taskinfo->fetch_row();\n\n    if ($row_taskinfo) {\n        $dataentry = array(\n            "pcode" => $row_taskinfo[0],\n            "modules" => $row_taskinfo[1],\n            "data_id" => 0,\n            "wavenum" => $row_taskinfo[2],\n            "prequest" => FALSE,\n            "highlight" => array()\n        );\n\n        $_SESSION['dataentry'] = $dataentry;\n    }\n    $stmt->close();\n}