// Define SSL context\n$context = stream_context_create([\n    'ssl' => [\n        'verify_peer'       => true,\n        'allow_self_signed' => false, // Ensure the client presents a certificate signed by your CA\n        'cafile'            => '/path/to/ca-bundle.crt', // CA bundle\n        'local_cert'        => '/path/to/server-crt.pem',\n        'local_pk'          => '/path/to/server-key.pem',\n    ],\n]);\n\n// Create a TCP/IP socket server\n$socket = stream_socket_server("ssl://0.0.0.0:8443", $errno, $errstr, STREAM_SERVER_BIND | STREAM_SERVER_LISTEN, $context);\n\nif (!$socket) {\n    echo "$errstr ($errno)\n";\n} else {\n    while (true) { // Run indefinitely to handle incoming connections\n        $clientSocket = stream_socket_accept($socket);\n        if ($clientSocket === false) {\n            break;\n        }\n        // Handle client communication here\n        fwrite($clientSocket, "Connection established\n");\n        fclose($clientSocket);\n    }\n    fclose($socket);\n}